import React, { useState } from 'react';
import PropTypes from 'prop-types';
import { Visitor } from '../../models/visitor.class';
import '../../styles/forms.css';

const VisitorComponent = () => {
  // Person Data
  const [fullName, setFullName] = useState('');
  const [email, setEmail] = useState('');
  const [phone, setPhone] = useState('');
  const [personalID, setPersonalID] = useState('');

  const handleSubmit = async (event) => {
    event.preventDefault();
    const response = await fetch('http://localhost:8000/person', {
    method: 'POST',
    headers: {
    'Content-Type': 'application/json'
    },
    body: JSON.stringify({
    full_name: fullName,
    email,
    phone,
    personal_isd: personalID
    })
    });
    const data = await response.json();
    console.log(data);
   }   

  return (
    <div>
      <div style={{ display: 'flex' }}>
        <div style={{ flex: 1 }}>
          <h2>Person Data</h2>
          <form className='va-form' onSubmit={handleSubmit}>
            <label>
              Full Name:
              <input type="text" value={fullName} onChange={event => setFullName(event.target.value)} />
            </label>
            <br />
            <label>
              Email:
              <input type="email" value={email} onChange={event => setEmail(event.target.value)} />
            </label>
            <br />
            <label>
              Phone:
              <input type="tel" value={phone} onChange={event => setPhone(event.target.value)} />
            </label>
            <br />
            <label>
              Personal ID:
              <input type="text" value={personalID} onChange={event => setPersonalID(event.target.value)} />
            </label>
            <br />
          </form>
        </div>
      </div>

      {/* Bot√≥n para enviar ambos formularios */}
      <input type="submit" value="Submit" onClick={handleSubmit} />
    </div>
  );
}

export default VisitorComponent;
