import React, { useState } from 'react';
import '../../styles/forms.css';

const CredentialComponent = () => {
  const [fullName, setFullName] = useState('');
  const [personalID, setPersonalID] = useState('');
  const [showImage, setShowImage] = useState(false);

  const handleSubmit = async (event) => {
    event.preventDefault();
    try {
      const requestData = {
        personalID
      };

      const response = await fetch('http://localhost:8000/people/{id}', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify(requestData)
      });

      if (response.ok) {
        const data = await response.json();
        console.log(data);

        // Verificar si el ID coincide con la dirección del fetch
        if (personalID === window.location.pathname.split('/').pop()) {
          // Generar código QR aleatorio
          const randomQRCode = generateRandomQRCode();
          console.log('Código QR generado:', randomQRCode);
        }
      } else {
        console.error('Error en la llamada al servicio create event');
      }
    } catch (error) {
      console.error('Error en la llamada al servicio create event', error);
    }
  };

  // Función para generar un código QR aleatorio
  const generateRandomQRCode = () => {
    const characters = 'ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789';
    let result = '';
    for (let i = 0; i < 10; i++) {
      result += characters.charAt(Math.floor(Math.random() * characters.length));
    }
    return result;
  };

  return (
    <div>
      <form className='c-form' onSubmit={handleSubmit}>
        <label>
          Full Name:
          <input type="text" value={fullName} onChange={(event) => setFullName(event.target.value)} />
        </label>
        <br />
        <label>
          Personal ID:
          <input type="text" value={personalID} onChange={(event) => setPersonalID(event.target.value)} />
        </label>
        <br />
        <input type="submit" value="Submit" />
      </form>

      {showImage && (
        <img style={{ marginTop: '5%' }} className="img-info" src="https://th.bing.com/th/id/OIP.qHRrge25gpFW2Kthj4Qs2AHaHa?w=181&h=181&c=7&r=0&o=5&pid=1.7" alt="" />
      )}
    </div>
  );
};

export default CredentialComponent;
