import React, { useState } from 'react';
import PropTypes from 'prop-types';
import { AuthorizedPersonnel } from '../../models/authorizedPersonnel.class';
import '../../styles/forms.css';

const AuthorizedPersonnelComponent = () => {
    // Person Data
    const [fullName, setFullName] = useState('');
    const [email, setEmail] = useState('');
    const [phone, setPhone] = useState('');
    const [personalID, setPersonalID] = useState('');

    // Event Data
    const [rolName, setRolName] = useState('');
    const [rolType, setRolType] = useState('');
    const [code, setCode] = useState('');

    const handleSubmit = (event) => {
        event.preventDefault();
        // Aquí puedes manejar el envío de ambos formularios
    }

    return (
        <div>
            <div style={{ display: 'flex' }}>
                <div style={{ flex: 1 }}>
                    <h2>Basic Info</h2>
                    <form className='va-form' onSubmit={handleSubmit}>
                        <label>
                            Full Name:
                            <input type="text" value={fullName} onChange={event => setFullName(event.target.value)} />
                        </label>
                        <br />
                        <label>
                            Email:
                            <input type="email" value={email} onChange={event => setEmail(event.target.value)} />
                        </label>
                        <br />
                        <label>
                            Phone:
                            <input type="tel" value={phone} onChange={event => setPhone(event.target.value)} />
                        </label>
                        <br />
                        <label>
                            Personal ID:
                            <input type="text" value={personalID} onChange={event => setPersonalID(event.target.value)} />
                        </label>
                    </form>
                </div>

                <div style={{ flex: 1 }}>
                    <h2>Advanced Info</h2>
                    <form className='va-form' onSubmit={handleSubmit}>
                        <label>
                            Rol Name:
                            <input type="text" value={rolName} onChange={event => setRolName(event.target.value)} />
                        </label>
                        <br />
                        <label>
                            Rol Type:
                            <input type="text" value={rolType} onChange={event => setRolType(event.target.value)} />
                        </label>
                        <br />
                        <label>
                            Code:
                            <input type="text" value={code} onChange={event => setCode(event.target.value)} />
                        </label>
                    </form>
                </div>
            </div>

            {/* Botón para enviar ambos formularios */}
            <input type="submit" value="Submit" onClick={handleSubmit} />
        </div>
    );
}

AuthorizedPersonnelComponent.propTypes = {
    fullName: PropTypes.instanceOf(AuthorizedPersonnel).isRequired,
    email: PropTypes.instanceOf(AuthorizedPersonnel).isRequired,
    phone: PropTypes.instanceOf(AuthorizedPersonnel).isRequired,
    personalID: PropTypes.instanceOf(AuthorizedPersonnel).isRequired,

    rolName: PropTypes.instanceOf(AuthorizedPersonnel).isRequired,
    rolType: PropTypes.instanceOf(AuthorizedPersonnel).isRequired,
    code: PropTypes.instanceOf(AuthorizedPersonnel).isRequired,
};

export default AuthorizedPersonnelComponent;
